"use strict";
$(document).ready(function() {
    $("body").smoothScroll({
        delegateSelector: 'a[href^="#"]',
        speed: 900
    }),
    $("body").scrollspy({
        target: "#topnav"
    }),
    $(document).scrollTop() < 50 ? $("body").removeClass("scrolled") : $("body").addClass("scrolled"),
    $(document).on("click", ".navbar-collapse.show", function(o) {
        $(o.target).is("a") && "dropdown-toggle" != $(o.target).attr("class") && $(this).collapse("hide")
    })
}),
$(window).scroll(function() {
    $(document).scrollTop() < 50 ? $("body").removeClass("scrolled") : $("body").addClass("scrolled")
}),
$("#topnavContent").bind("show.bs.collapse", function() {
    $("#topnav").addClass("collapse-visible")
}),
$("#topnavContent").bind("hidden.bs.collapse", function() {
    $("#topnav").removeClass("collapse-visible")
});

